import React from 'react';
import Map from './Map';
import Toolbox from './Toolbox';

let x,y,pokemonId;
class App extends React.Component {

    pokemonCall(pokemonId) {
        this.pokemonId = pokemonId;
    }


    area = (xCoor,yCoor) => {
        x = xCoor;
        y = yCoor;
    }


    render() {
        return (
            <div className="row">
                <div className="col-6">
                    <Map 
                    x = {x ? x : 51.505}
                    y = {y ? y : -0.09}


                    />

                </div>
                <div className="col-6">
                    <Toolbox 
                    area={this.area}
                    pokemonCall={this.pokemonCall} />
                </div>
            </div>
        );
    }

};
export default App;
