import React from 'react';
import Map from './Map';
import Toolbox from './Toolbox';

let x,y;
class App extends React.Component {

    area = (xCoor,yCoor) => {
        x = xCoor;
        y = yCoor;
    }


    render() {
        return (
            <div className="row">
                <div className="col-6">
                    x = {x ? x : 51.505}
                    y = {y ? y : -0.09}
                    <Map onChange={() => {alert("changed")}}

                    />
                </div>
                <div className="col-6">
                    <Toolbox area={this.area} />
                </div>
            </div>
        );
    }

};
export default App;
