import React from 'react';
import L from 'leaflet';


var mymap, circle;
//Stateless Map Function
class Map extends React.Component {

    state = {
        logs: "",
        x: 0,
        y: 0,
        pokemon: null,
        pokemonsFaction1: [
            {
                Name: "Pikachu",
                Icon: "https://www.flaticon.com/svg/static/icons/svg/188/188987.svg",
                AttackPower: "",
                DefensePower: "",
                TotalHealth: "",
                id: 1,
                canUsable: true
            },
            {
                Name: "Bulbasaur",
                Icon: "http://www.rw-designer.com/icon-image/14434-256x256x8.png",
                AttackPower: "",
                DefensePower: "",
                TotalHealth: "",
                id: 2,
                canUsable: true
            },
            {
                Name: "Snorlax",
                Icon: "https://www.flaticon.com/svg/static/icons/svg/189/189001.svg",
                AttackPower: "",
                DefensePower: "",
                TotalHealth: "",
                id: 3,
                canUsable: true
            }
        ],
        pokemonsFaction2: [
            {
                Name: "Charizard",
                Icon: "https://cdn.dribbble.com/users/1088894/screenshots/2631953/charizard-shot.jpg?compress=1&resize=800x600",
                AttackPower: "",
                DefensePower: "",
                TotalHealth: "",
                id: 4,
                canUsable: true
            },
            {
                Name: "Mewtwo",
                Icon: "https://i.pinimg.com/originals/ef/07/74/ef0774a46a2116d3201c252459328540.jpg",
                AttackPower: "",
                DefensePower: "",
                TotalHealth: "",
                id: 5,
                canUsable: true
            },
            {
                Name: "Ditto",
                Icon: "https://cdn2.iconfinder.com/data/icons/pokemon-filledoutline/64/ditto-pokemon-nintendo-video-game-gaming-gartoon-monster-512.png",
                AttackPower: "",
                DefensePower: "",
                TotalHealth: "",
                id: 6,
                canUsable: true
            }
        ]
    }



    componentDidMount() {

        mymap = L.map('mapid').setView([51.505, -0.09], 13);
        //var pokemon1 = L.marker([x, y]).addTo(mymap);
        //var pokemon2 = L.marker([51.5, -0.10]).addTo(mymap);

        /*

        */

        L.tileLayer('https://api.mapbox.com/styles/v1/{id}/tiles/{z}/{x}/{y}?access_token={accessToken}', {
            attribution: 'Map data &copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors, Imagery © <a href="https://www.mapbox.com/">Mapbox</a>',
            maxZoom: 18,
            id: 'mapbox/streets-v11',
            tileSize: 512,
            zoomOffset: -1,
            accessToken: 'pk.eyJ1IjoiZGd1bHlhc2FyIiwiYSI6ImNrajYwZjdoYzYzNTIyc2xiNGkwZHFibW0ifQ.mqAXzMxXZJ197aIL5DsOQA'
        }).addTo(mymap);

    }

    componentDidUpdate() {


    }


    createArea = (xCoor, yCoor) => {
        this.state.x = xCoor;
        this.state.y = yCoor;

        if (!mymap) {
            mymap = L.map('mapid').setView([51.505, -0.09], 13);
        } else {
            circle = L.circle([this.state.x, this.state.y], {
                color: 'red',
                fillColor: 'red',
                fillOpacity: 0.5,
                radius: 1500
            }).addTo(mymap);
        }

        this.render();

    }



    setChanges = (pokemonProp) => {

            //Düzenleme gerekli sonsuz döngüye giriyor bir şekilde setState olması gerekiyor

            console.log(pokemonProp)

            var pokemonIcon = L.icon({
                iconUrl: pokemonProp.Icon,
                shadowUrl: pokemonProp.Icon,

                iconSize: [38, 64], // size of the icon
                shadowSize: [0, 0], // size of the shadow
                iconAnchor: [22, 94], // point of the icon which will correspond to marker's location
                shadowAnchor: [4, 62],  // the same for the shadow
                popupAnchor: [-3, -76] // point from which the popup should open relative to the iconAnchor

            });

            //Adds Marker to the map

            var pokemon1 = L.marker([this.state.x, this.state.y], { icon: pokemonIcon, draggable:true }, ).addTo(mymap).on('click',() =>  {
                pokemon1.bindPopup(
                    pokemonProp.Name + "<br> <input id='attPow' />Attack Power <br/> <br> <input id='defPow' />Defense Power <br/> <br> <input id='totHealth' />Total Health</input><br/> <button onClick='alert('Hello')'>Submit </button>" 
                ).openPopup().on('click',()=>{

                });
            });




            let firstLat = pokemon1.getLatLng().lat;
            let firstLng = pokemon1.getLatLng().lng;


            pokemon1.on ('dragend', function (e) {
                    let currentLat = pokemon1.getLatLng().lat; 
                    let currentLng = pokemon1.getLatLng().lng;                                       
                    if(currentLat<(firstLat)+0.01 && currentLat>(firstLat)-0.01 && currentLng<(firstLng)+0.025 && currentLng>(firstLng)-0.025 ) {
                        document.getElementById('latitude').value = pokemon1.getLatLng().lat;    
                        document.getElementById('longitude').value = pokemon1.getLatLng().lng;
                    }else {
                        pokemon1.getLatLng().lat=firstLat;
                        pokemon1.getLatLng().lng=firstLng;
                    }


                    let newPosLat = pokemon1.getLatLng().lat
                    let newPosLng = pokemon1.getLatLng().lng
                    //logs.innerHTML = "pokemonProp.Name  + newPosLat +  + newPosLng"





        });







    }


    Attack = (attPow,defPow,totHealth) => {
      console.log(attPow,defPow,totHealth)

    }

    render() {
        return (
            <div>
                <div className="btn-group" role="group" aria-label="Basic mixed styles example">
                    <button type="button" className="btn btn-danger" onClick={() => { this.createArea(prompt("X"), prompt("Y")) }}>Create area </button>
                </div>

                <form hidden>
                    <label htmlFor="latitude">Latitude:</label>
                    <input id="latitude" type="text" />
                    <label htmlFor="longitude">Longitude:</label>
                    <input id="longitude" type="text" />
                </form>

                <div id="mapid" style={{ width: "100%", height: "800px" }} />


                <div className="btn-group" role="group" aria-label="Basic mixed styles example">
                    <button type="button" className="btn btn-danger" onClick={() => { this.setChanges(this.props.pokemonId)}}>Put Pokemon </button>
                </div>

                <p id="Logs"> {this.state.logs} </p>




            </div>


        );
    }


};

export default Map;
