import React from 'react';
import L from 'leaflet';

//Stateless Map Function
class Map extends React.Component {

    state = {
        x: 0,
        y: 0,
    }


    componentDidMount() {

        var mymap = L.map('mapid').setView([51.505, -0.09], 13);
        //var pokemon1 = L.marker([x, y]).addTo(mymap);
        //var pokemon2 = L.marker([51.5, -0.10]).addTo(mymap);

        /*

        */

        L.tileLayer('https://api.mapbox.com/styles/v1/{id}/tiles/{z}/{x}/{y}?access_token={accessToken}', {
            attribution: 'Map data &copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors, Imagery © <a href="https://www.mapbox.com/">Mapbox</a>',
            maxZoom: 18,
            id: 'mapbox/streets-v11',
            tileSize: 512,
            zoomOffset: -1,
            accessToken: 'pk.eyJ1IjoiZGd1bHlhc2FyIiwiYSI6ImNrajYwZjdoYzYzNTIyc2xiNGkwZHFibW0ifQ.mqAXzMxXZJ197aIL5DsOQA'
        }).addTo(mymap);

    }

    componentDidUpdate() {
        if(!mymap) {
            mymap = L.map('mapid').setView([51.505, -0.09], 13);
        }else {
            var circle = L.circle([this.state.x, this.state.y], {
                color: 'red',
                fillColor: 'red',
                fillOpacity: 0.5,
                radius: 500
            }).addTo(mymap);
        }


    }

    createArea(xCoor,yCoor) {
        this.state.x = xCoor;
        this.state.y = yCoor;
        this.componentDidUpdate();


        console.log(this.state.x, this.state.y)
    }



    render() {
        return (
            <div>
                <div className="btn-group" role="group" aria-label="Basic mixed styles example">
                        <button type="button" className="btn btn-danger" onClick={ () => {this.createArea(prompt("X"),prompt("Y"))} }>Create area </button>
                </div>


                <div id="mapid" style={{ width: "100%", height: "800px" }} />







            </div>


        );
    }


};

export default Map;
